import React, { Component } from 'react'
//import EmployeeService from '../services/EmployeeService'
import drugService from '../services/drugService'
import Navbar from './Navbar'

class ViewDrugs extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            drugs: {}
        }
    }

    componentDidMount(){
        drugService.getDrugById(this.state.id).then( res => {
            this.setState({drugs: res.data});
        })
    }

    render() {
        return (
            <div>
                <Navbar/>

                <br></br>
                <div className = "card col-md-6 offset-md-3">
                    <h3 className = "text-center"> View Drug Details</h3>
                    <div className = "card-body">
                        <div className='row' >
                            <label> Drug Name:   <i> { this.state.drugs.drugName }</i></label>
                            
                        </div>
                        <div className = "row">
                            <label> Drug Price: </label>
                            <div> { this.state.drugs.drugPrice }</div>
                        </div>
                        <div className = "row">
                            <label> Drug Quantity: </label>
                            <div> { this.state.drugs.drugQuantity }</div>
                        </div>
                        <div className = "row">
                            <label> Drug Expiry Date: </label>
                            <div> { this.state.drugs.expiryDate }</div>
                        </div>
                    </div>

                </div>
            </div>
        )
    }
}

export default ViewDrugs
