import React from "react";
import axios from "axios";
 export default function Drugs(){
  const base_url= "http://localhost:8082/doctors/drugs/";

  const render_products = (DrugsData) => {

    return <div className='category-section fixed'>
      <h2 className="text-3xl font-extrabold tracking-tight text-gray-600 category-title">Drugs ({DrugsData.length})</h2>

      <div className="m-6 p-3 mt-10 ml-0 grid grid-cols-1 gap-y-10 gap-x-6 sm:grid-cols-2 lg:grid-cols-6 xl:gap-x-10" style={{ maxHeight: '800px', overflowY: 'scroll' }}>
        {/* Loop Products */}
        {DrugsData.map((drugsData, index) => (
          <div key={index} className="group relative shadow-lg" >
            {/* <div className=" min-h-80 bg-gray-200 aspect-w-1 aspect-h-1 rounded-md overflow-hidden group-hover:opacity-75 lg:h-60 lg:aspect-none">
              <img
                alt="Product Image"
                src={product.image}
                className="w-full h-full object-center object-cover lg:w-full lg:h-full"
              />
            </div> */}
            <div className="flex justify-between p-3">
              <div>
                <h3 className="text-sm text-gray-700">
                  <a href={drugsData.href}>
                    <span aria-hidden="true" className="absolute inset-0" />
                    <span style={{ fontSize: '16px', fontWeight: '600' }}>{drugsData.drugName}</span>
                  </a>
                 
                </h3>
                <p className="mt-1 text-sm text-gray-500">Expiry Date: {drugsData.expiryDate}</p>
              </div>
              <p className="text-sm font-medium text-green-600">${drugPrice.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  }

  const [DrugsData, setDrugsData] = React.useState(null);
  
  const [error, setError] = React.useState(null);

  // Get all Products.
  React.useEffect(() => {

    axios.get(base_url +"all").then((response) => {

      setDrugsData(response.data);
      console.log(DrugsData);

    }).catch(error => {

      setError(error);
    });

  }, []);
  

 }
