import React, { Component } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import { isEmail } from "validator";
import { Link } from "react-router-dom";
import Navbar_beforeLogin from "./Navbar_beforeLogin";
import './login.css'
import orderService from "../services/orderService";

import { connect } from "react-redux";
//import { register } from "../actions/auth";

const required = (value) => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

const date = (value) => {
  if (!Date(value)) {
    return (
      <div className="alert alert-danger" role="alert">
        This is not a valid date.
      </div>
    );
  }
};

const quantity = (value) => {
  if (value < 1 || value > 30) {
    return (
      <div className="alert alert-danger" role="alert">
        The quantity must be between 3 and 20 characters.
      </div>
    );
  }
};
const drugname = (value) => {
  if (value.length === 0) {
    return (
      <div className="alert alert-danger" role="alert">
        Enter valid drug name.
      </div>
    );
  }
};

const doctorname = (value) => {
  if (value.length < 6 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The doctor name is incorrect.
      </div>
    );
  }
};

class AddOrder extends Component {
  constructor(props) {
    super(props);
    this.handleAddOrder = this.handleAddOrder.bind(this);
    this.onChangedate = this.onChangedate.bind(this);
    this.onChangequantity = this.onChangequantity.bind(this);
    this.onChangedrugname = this.onChangedrugname.bind(this);
    this.onChangedoctorname = this.onChangedoctorname.bind(this);

    this.state = {
      date: "",
      quantity: "",
      drugname: "",
      doctorname: "",
      successful: false,
    };
  }

  onChangedate(e) {
    this.setState({
      datename: e.target.value,
    });
  }
  onChangequantity(e) {
    this.setState({
      quantity: e.target.value,
    });
  }
  onChangedrugname(e) {
    this.setState({
      drugname: e.target.value,
    });
  }

  onChangedoctorname(e) {
    this.setState({
      doctorname: e.target.value,
    });
  }

  handleAddOrder(e) {
    e.preventDefault();

    this.setState({
      successful: false,
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      this.props
        .dispatch(
          orderService(
            this.state.date,
            this.state.quantity,
            this.state.drugname,
            this.state.doctorname
          )
        )
        .then(() => {
          this.setState({
            successful: true,
            
          });
         // history.push("/login");
          alert("Ordered successfully");
          console.log("order successfully");
        })
        .catch(() => {
          this.setState({
            successful: false,
          });
        });
    }
  }

  render() {
    const { message } = this.props;

    return (
      <div>
        <Navbar_beforeLogin />       

        <Form
          onSubmit={this.handleAddOrder}
          ref={(c) => {
            this.form = c;
          }}
        >
         
        <div className="container-fluid ps-md-0" style={{ marginTop: "0px" }}>         
            
            
            
      
        

        <div className="col-md-4 bg-image" style={{ margin: "50px" }}>
          <div className="col-md-12 card bg-light text-dark" style={{ marginLeft: "700px" }} >
            <h1>
              <center>Place your Order</center>
            </h1>

              {!this.state.successful && (
                <div>
                  <div className="form-group">
                    <Input
                      type="Date"
                      className="form-control"
                      name="date"
                      value={this.state.date}
                      onChange={this.onChangedate}
                      validations={[required, date]}
                      placeholder="Enter the date"
                    />
                  </div>

                  <div className="form-group">
                    <Input
                      type="number"
                      className="form-control"
                      name="quanityt"
                      value={this.state.quantity}
                      onChange={this.onChangequantity}
                      validations={[required, quantity]}
                      placeholder="Enter the no of drugs you want"
                    />
                  </div>

                  <div className="form-group">
                    <Input
                      type="text"
                      className="form-control"
                      name="drugname"
                      value={this.state.drugname}
                      onChange={this.onChangedrugname}
                      validations={[required, drugname]}
                      placeholder="Enter the drugname"
                    />
                  </div>

                  <div className="form-group">
                    <Input
                      type="text"
                      className="form-control"
                      name="doctorname"
                      value={this.state.doctorname}
                      onChange={this.onChangedoctorname}
                      validations={[required, doctorname]}
                      placeholder="Enter your name"
                    />
                  </div>

                  <div className="form-group">
                    <button className="btn btn-dark btn-block">Order</button>
                    <h2></h2>
                    <p>
                      <i>
                        <h6>
                          Already Have an Account ?{" "}
                          <Link to="/login" className="formFieldLink">
                            Sign In
                          </Link>
                        </h6>
                      </i>
                    </p>
                  </div>
                  {/* <p className="mt-3">Already Have an Account <a href="login.components.js">Sign In</a></p> */}
                </div>
              )}

              {message && (
                <div className="form-group">
                  <div
                    className={
                      this.state.successful
                        ? "alert alert-success"
                        : "alert alert-danger"
                    }
                    role="alert"
                  >
                    {message}
                  </div>
                </div>
              )}
              <CheckButton
                style={{ display: "none" }}
                ref={(c) => {
                  this.checkBtn = c;
                }}
              />
            
          </div>
        </div>
      </div>
      </Form>
      </div>

    );
  }
}

function mapStateToProps(state) {
  const { message } = state.message;
  return {
    message,
  };
}

export default connect(mapStateToProps)(AddOrder);
