import React, { Component } from 'react'
//import EmployeeService from '../services/EmployeeService'
import drugService from '../services/drugService';

class ListDrugs extends Component {
    constructor(props) {
        super(props)

        this.state = {
                drugs: []
        }
        this.addDrugs = this.addDrugs.bind(this);
        this.editDrugs = this.updateDrugs.bind(this);
        this.deleteDrugs = this.deleteDrugs.bind(this);
    }

    deleteDrugs(id){
        drugService.deleteDrugs(id).then( res => {
            this.setState({drugs: this.state.drugss.filter(drug => drug.id !== id)});
        });
    }
    viewDrugs(id){
        this.props.history.push(`/view-drugs/${id}`);
    }
    updateDrugs(id){
        this.props.history.push(`/add-drugs/${id}`);
    }

    componentDidMount(){
        drugService.getAllDrugs().then((res) => {
            this.setState({ drugs: res.data});
        });
    }

    addDrugs(){
        this.props.history.push('/add-drug/save');
    }

    render() {
        return (
            <div>
                 <h2 className="text-center">Drugs List</h2>
                 <div className = "row">
                    <button className="btn btn-primary" onClick={this.addDrugs}> Add Drugs</button>
                 </div>
                 <br></br>
                 <div className = "row">
                        <table className = "table table-striped table-bordered">

                            <thead>
                                <tr>
                                    <th> Drug Name</th>
                                    <th> Drug Price</th>
                                    <th> Drug Quantity</th>
                                    <th> Expiry Date</th>
                                    <th> Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.drugs.map(
                                        drug => 
                                        <tr key = {drug.id}>
                                             <td> { drug.drugName} </td>   
                                             <td> {drug.drugPrice}</td>
                                             <td> {drug.drugQuantity}</td>
                                             <td> {drug.expiryDate}</td>
                                             <td>
                                                 <button onClick={ () => this.updateDrugs(drug.id)} className="btn btn-info">Update </button>
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.deleteDrugs(drug.id)} className="btn btn-danger">Delete </button>
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.viewDrugs(drug.id)} className="btn btn-info">View </button>
                                             </td>
                                        </tr>
                                    )
                                }
                            </tbody>
                        </table>

                 </div>

            </div>
        )
    }
}

export default ListDrugs
