import axios from 'axios';

const DRUG_API_BASE_URL = "http://localhost:8081/";

class DrugService {

    getAllDrugs(){
        return axios.get(DRUG_API_BASE_URL + 'doctors/drugs/all');
    }

    saveDrugs(drugs){
        return axios.post(DRUG_API_BASE_URL + 'drugs/save', drugs);
    }

    getDrugById(drugId){
        return axios.get(DRUG_API_BASE_URL + 'admin/drugs/' + drugId);
    }

    updateDrugs(drugs, drugId){
        return axios.put(DRUG_API_BASE_URL + 'drugs/update/' + drugId, drugs);
    }

    deleteDrugs(drugId){
        return axios.delete(DRUG_API_BASE_URL + 'admin/delete/' + drugId);
    }
}

export default new DrugService()
